import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './SuperAdminDashboard.css';

interface Hospital {
  _id: string;
  name: string;
  location: string;
  description: string;
}

interface HospitalAdmin {
  _id: string;
  name: string;
  email: string;
  role: string;
  hospitalId: string;
}

const SuperAdminDashboard: React.FC = () => {
  const navigate = useNavigate();
  const [hospitals, setHospitals] = useState<Hospital[]>([]);
  const [newHospital, setNewHospital] = useState({
    name: '',
    location: '',
    description: '',
  });
  const [newAdmin, setNewAdmin] = useState({
    name: '',
    email: '',
    password: '',
    hospitalId: '',
  });
  const [error, setError] = useState<string>('');
  const [success, setSuccess] = useState<string>('');
  const [activeTab, setActiveTab] = useState<'hospitals' | 'admins'>('hospitals');
  const [loading, setLoading] = useState<boolean>(true);

  useEffect(() => {
    // Check if user is super admin
    const userRole = localStorage.getItem('userRole');
    console.log('Current user role:', userRole); // Debug log
    if (userRole !== 'SUPER_ADMIN') {
      console.log('Unauthorized access attempt. Redirecting to login...'); // Debug log
      navigate('/login');
      return;
    }
    fetchHospitals();
  }, [navigate]);

  const fetchHospitals = async () => {
    setLoading(true);
    setError('');
    try {
      console.log('Fetching hospitals...');
      const response = await axios.get('http://localhost:3000/api/auth/hospitals', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json'
        }
      });
      console.log('Hospitals response:', response.data);
      setHospitals(response.data);
    } catch (err: any) {
      console.error('Error fetching hospitals:', err);
      setError(err.response?.data?.message || 'Failed to fetch hospitals');
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('userRole');
    localStorage.removeItem('userEmail');
    localStorage.removeItem('token');
    navigate('/login');
  };

  const handleAddHospital = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const response = await axios.post('http://localhost:3000/api/auth/hospitals', newHospital, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json'
        }
      });
      setHospitals([...hospitals, response.data]);
      setNewHospital({ name: '', location: '', description: '' });
      setSuccess('Hospital added successfully');
    } catch (err: any) {
      console.error('Error adding hospital:', err);
      setError(err.response?.data?.message || 'Failed to add hospital');
    }
  };

  const handleAddAdmin = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const response = await axios.post('http://localhost:3000/api/auth/create-admin', {
        ...newAdmin,
        role: 'HOSPITAL_ADMIN',
      }, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json'
        }
      });
      setSuccess('Hospital admin created successfully');
      setNewAdmin({ name: '', email: '', password: '', hospitalId: '' });
    } catch (err: any) {
      console.error('Error creating admin:', err);
      setError(err.response?.data?.message || 'Failed to create hospital admin');
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    if (name in newHospital) {
      setNewHospital(prev => ({ ...prev, [name]: value }));
    } else {
      setNewAdmin(prev => ({ ...prev, [name]: value }));
    }
  };

  return (
    <div className="super-admin-dashboard">
      <header className="dashboard-header">
        <h1>Super Admin Dashboard</h1>
        <button onClick={handleLogout} className="logout-button">Logout</button>
      </header>

      <main className="dashboard-content">
        <div className="tabs">
          <button 
            className={`tab-button ${activeTab === 'hospitals' ? 'active' : ''}`}
            onClick={() => setActiveTab('hospitals')}
          >
            Manage Hospitals
          </button>
          <button 
            className={`tab-button ${activeTab === 'admins' ? 'active' : ''}`}
            onClick={() => setActiveTab('admins')}
          >
            Manage Hospital Admins
          </button>
        </div>

        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">{success}</div>}

        {activeTab === 'hospitals' ? (
          <>
            <section className="add-hospital-section">
              <h2>Add New Hospital</h2>
              <form onSubmit={handleAddHospital} className="add-hospital-form">
                <div className="form-group">
                  <label htmlFor="name">Hospital Name:</label>
                  <input
                    type="text"
                    id="name"
                    name="name"
                    value={newHospital.name}
                    onChange={handleInputChange}
                    required
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="location">Location:</label>
                  <input
                    type="text"
                    id="location"
                    name="location"
                    value={newHospital.location}
                    onChange={handleInputChange}
                    required
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="description">Description:</label>
                  <textarea
                    id="description"
                    name="description"
                    value={newHospital.description}
                    onChange={handleInputChange}
                    required
                  />
                </div>
                <button type="submit" className="add-button">Add Hospital</button>
              </form>
            </section>

            <section className="hospitals-list-section">
              <h2>Registered Hospitals</h2>
              {loading ? (
                <div className="loading-message">Loading hospitals...</div>
              ) : hospitals.length === 0 ? (
                <div className="no-hospitals-message">No hospitals registered yet.</div>
              ) : (
                <div className="hospitals-list">
                  {hospitals.map(hospital => (
                    <div key={hospital._id} className="hospital-card">
                      <h3>{hospital.name}</h3>
                      <p><strong>Location:</strong> {hospital.location}</p>
                      <p><strong>Description:</strong> {hospital.description}</p>
                      <div className="hospital-actions">
                        <button className="edit-button">Edit</button>
                        <button className="delete-button">Delete</button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </section>
          </>
        ) : (
          <section className="add-admin-section">
            <h2>Create Hospital Admin</h2>
            <form onSubmit={handleAddAdmin} className="add-admin-form">
              <div className="form-group">
                <label htmlFor="adminName">Admin Name:</label>
                <input
                  type="text"
                  id="adminName"
                  name="name"
                  value={newAdmin.name}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="adminEmail">Email:</label>
                <input
                  type="email"
                  id="adminEmail"
                  name="email"
                  value={newAdmin.email}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="adminPassword">Password:</label>
                <input
                  type="password"
                  id="adminPassword"
                  name="password"
                  value={newAdmin.password}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="hospitalId">Select Hospital:</label>
                <select
                  id="hospitalId"
                  name="hospitalId"
                  value={newAdmin.hospitalId}
                  onChange={handleInputChange}
                  required
                >
                  <option value="">Select a hospital</option>
                  {hospitals.map(hospital => (
                    <option key={hospital._id} value={hospital._id}>
                      {hospital.name}
                    </option>
                  ))}
                </select>
              </div>
              <button type="submit" className="add-button">Create Admin</button>
            </form>
          </section>
        )}
      </main>
    </div>
  );
};

export default SuperAdminDashboard;
