import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './HospitalAdminDashboard.css';

interface Doctor {
  _id: string;
  name: string;
  email: string;
  specialization: string;
  departmentId: string;
  department: {
    name: string;
  };
  hospitalId: string;
  available: boolean;
}

interface Nurse {
  _id: string;
  name: string;
  email: string;
  departmentId: string;
  department?: {
    name: string;
  };
}

interface Department {
  _id: string;
  name: string;
  description: string;
  hospitalId: string;
}

interface Hospital {
  _id: string;
  name: string;
}

interface NewItem {
  name: string;
  email: string;
  password: string;
  departmentId: string;
  specialization: string;
  description?: string;
  hospitalId?: string;
}

const HospitalAdminDashboard: React.FC = () => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('doctors');
  const [doctors, setDoctors] = useState<Doctor[]>([]);
  const [nurses, setNurses] = useState<Nurse[]>([]);
  const [departments, setDepartments] = useState<Department[]>([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(true);
  const [hospital, setHospital] = useState<Hospital | null>(null);
  const [adminName, setAdminName] = useState<string>('');
  const [showAddForm, setShowAddForm] = useState(false);
  const [newItem, setNewItem] = useState<NewItem>({
    name: '',
    email: '',
    password: '',
    departmentId: '',
    specialization: ''
  });

  useEffect(() => {
    const userRole = localStorage.getItem('userRole');
    const hospitalId = localStorage.getItem('hospitalId');
    const name = localStorage.getItem('userName');
    
    if (userRole !== 'HOSPITAL_ADMIN' || !hospitalId) {
      navigate('/login');
      return;
    }

    setAdminName(name || '');
    fetchHospitalDetails(hospitalId);
    fetchData();
  }, [navigate]);

  const fetchHospitalDetails = async (hospitalId: string) => {
    try {
      const response = await axios.get(`http://localhost:3000/api/auth/hospitals/${hospitalId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setHospital(response.data);
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to fetch hospital details');
    }
  };

  const fetchData = async () => {
    setLoading(true);
    setError('');
    try {
      const hospitalId = localStorage.getItem('hospitalId');
      const token = localStorage.getItem('token');

      if (!hospitalId || !token) {
        throw new Error('Missing hospital ID or token');
      }

      // Fetch departments
      const departmentsResponse = await axios.get(`http://localhost:3000/api/dept/hospital/${hospitalId}`, {
        headers: { 
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      
      if (departmentsResponse.data) {
        setDepartments(departmentsResponse.data);
      }

      // Fetch doctors
      const doctorsResponse = await axios.get(`http://localhost:3000/api/doctor/hospital/${hospitalId}`, {
        headers: { 
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      
      if (doctorsResponse.data) {
        setDoctors(doctorsResponse.data);
      }

      // Fetch nurses
      const nursesResponse = await axios.get(`http://localhost:3000/api/nurse/hospital/${hospitalId}`, {
        headers: { 
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      
      if (nursesResponse.data) {
        setNurses(nursesResponse.data);
      }

    } catch (err: any) {
      console.error('Error fetching data:', err);
      if (err.response) {
        setError(err.response.data?.message || 'Failed to fetch data from server');
      } else if (err.request) {
        setError('No response received from server');
      } else {
        setError('Error setting up the request');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  const handleAddDoctor = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const hospitalId = localStorage.getItem('hospitalId');
      const token = localStorage.getItem('token');

      if (!hospitalId || !token) {
        throw new Error('Missing hospital ID or token');
      }

      const response = await axios.post('http://localhost:3000/api/doctor/create-doctor', {
        ...newItem,
        hospitalId,
        role: 'DOCTOR'
      }, {
        headers: { 
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.data) {
        setDoctors(prev => [...prev, response.data]);
        setSuccess('Doctor added successfully');
        setNewItem({ 
          name: '', 
          email: '', 
          password: '', 
          departmentId: '', 
          specialization: '' 
        });
        setShowAddForm(false);
      }
    } catch (err: any) {
      console.error('Error adding doctor:', err);
      setError(err.response?.data?.message || 'Failed to add doctor');
    }
  };

  const handleAddNurse = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const hospitalId = localStorage.getItem('hospitalId');
      const response = await axios.post('http://localhost:3000/api/nurse', {
        ...newItem,
        hospitalId,
        role: 'NURSE'
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });

      setNurses(prev => [...prev, response.data]);
      setSuccess('Nurse added successfully');
      setNewItem({ name: '', email: '', password: '', departmentId: '', specialization: '' });
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to add nurse');
    }
  };

  const handleAddDepartment = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const hospitalId = localStorage.getItem('hospitalId');
      const token = localStorage.getItem('token');

      if (!hospitalId || !token) {
        throw new Error('Missing hospital ID or token');
      }

      const response = await axios.post('http://localhost:3000/api/dept/create-dept', {
        name: newItem.name,
        description: newItem.specialization,
        hospitalId
      }, {
        headers: { 
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.data) {
        setDepartments(prev => [...prev, response.data]);
        setSuccess('Department added successfully');
        setNewItem({ 
          name: '', 
          email: '', 
          password: '', 
          departmentId: '', 
          specialization: '' 
        });
        setShowAddForm(false);
      }
    } catch (err: any) {
      console.error('Error adding department:', err);
      setError(err.response?.data?.message || 'Failed to add department');
    }
  };

  const handleDeleteDoctor = async (doctorId: string) => {
    if (!window.confirm('Are you sure you want to delete this doctor?')) return;

    try {
      await axios.delete(`http://localhost:3000/api/doctor/${doctorId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setDoctors(prev => prev.filter(d => d._id !== doctorId));
      setSuccess('Doctor deleted successfully');
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to delete doctor');
    }
  };

  const handleDeleteNurse = async (nurseId: string) => {
    if (!window.confirm('Are you sure you want to delete this nurse?')) return;

    try {
      await axios.delete(`http://localhost:3000/api/nurse/${nurseId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setNurses(prev => prev.filter(n => n._id !== nurseId));
      setSuccess('Nurse deleted successfully');
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to delete nurse');
    }
  };

  const handleDeleteDepartment = async (departmentId: string) => {
    if (!window.confirm('Are you sure you want to delete this department?')) return;

    try {
      await axios.delete(`http://localhost:3000/api/dept/${departmentId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setDepartments(prev => prev.filter(d => d._id !== departmentId));
      setSuccess('Department deleted successfully');
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to delete department');
    }
  };

  const handleEditDoctor = async (doctorId: string) => {
    // TODO: Implement edit functionality
    console.log('Edit doctor:', doctorId);
  };

  const handleEditDepartment = async (departmentId: string) => {
    // TODO: Implement edit functionality
    console.log('Edit department:', departmentId);
  };

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <div className="header-content">
          <h1>Hospital Admin Dashboard</h1>
          <button className="logout-btn" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </header>

      <div className="dashboard-stats">
        <div className="stat-card">
          <div className="stat-icon">👨‍⚕️</div>
          <div className="stat-info">
            <h3>Total Doctors</h3>
            <p>{doctors.length}</p>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">🏥</div>
          <div className="stat-info">
            <h3>Departments</h3>
            <p>{departments.length}</p>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">📅</div>
          <div className="stat-info">
            <h3>Appointments</h3>
            <p>0</p>
          </div>
        </div>
      </div>

      <div className="dashboard-content">
        <div className="tab-navigation">
          <button 
            className={`tab-btn ${activeTab === 'doctors' ? 'active' : ''}`}
            onClick={() => setActiveTab('doctors')}
          >
            Doctors
          </button>
          <button 
            className={`tab-btn ${activeTab === 'departments' ? 'active' : ''}`}
            onClick={() => setActiveTab('departments')}
          >
            Departments
          </button>
        </div>

        <div className="tab-content">
          {activeTab === 'doctors' && (
            <div className="doctors-section">
              <div className="section-header">
                <h2>Manage Doctors</h2>
                <button 
                  className="add-btn"
                  onClick={() => setShowAddForm(!showAddForm)}
                >
                  {showAddForm ? 'Cancel' : 'Add Doctor'}
                </button>
              </div>

              {showAddForm && (
                <div className="add-form-container">
                  <form onSubmit={handleAddDoctor} className="add-form">
                    <div className="form-group">
                      <input
                        type="text"
                        placeholder="Name"
                        value={newItem.name}
                        onChange={(e) => setNewItem({...newItem, name: e.target.value})}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <input
                        type="email"
                        placeholder="Email"
                        value={newItem.email}
                        onChange={(e) => setNewItem({...newItem, email: e.target.value})}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <input
                        type="password"
                        placeholder="Password"
                        value={newItem.password}
                        onChange={(e) => setNewItem({...newItem, password: e.target.value})}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <input
                        type="text"
                        placeholder="Specialization"
                        value={newItem.specialization}
                        onChange={(e) => setNewItem({...newItem, specialization: e.target.value})}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <select
                        value={newItem.departmentId}
                        onChange={(e) => setNewItem({...newItem, departmentId: e.target.value})}
                        required
                      >
                        <option value="">Select Department</option>
                        {departments.map(dept => (
                          <option key={dept._id} value={dept._id}>
                            {dept.name}
                          </option>
                        ))}
                      </select>
                    </div>
                    <button type="submit" className="submit-btn">Add Doctor</button>
                  </form>
                </div>
              )}

              {loading ? (
                <div className="loading-spinner">Loading...</div>
              ) : error ? (
                <div className="error-message">{error}</div>
              ) : (
                <div className="doctors-grid">
                  {doctors.map(doctor => (
                    <div key={doctor._id} className="doctor-card">
                      <div className="doctor-header">
                        <h3>{doctor.name}</h3>
                        <span className={`status-badge ${doctor.available ? 'available' : 'unavailable'}`}>
                          {doctor.available ? 'Available' : 'Unavailable'}
                        </span>
                      </div>
                      <div className="doctor-info">
                        <p><strong>Email:</strong> {doctor.email}</p>
                        <p><strong>Department:</strong> {doctor.department?.name || 'Not assigned'}</p>
                        <p><strong>Specialization:</strong> {doctor.specialization}</p>
                      </div>
                      <div className="doctor-actions">
                        <button 
                          className="edit-btn"
                          onClick={() => handleEditDoctor(doctor._id)}
                        >
                          Edit
                        </button>
                        <button 
                          className="delete-btn"
                          onClick={() => handleDeleteDoctor(doctor._id)}
                        >
                          Delete
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}

          {activeTab === 'departments' && (
            <div className="departments-section">
              <div className="section-header">
                <h2>Manage Departments</h2>
                <button 
                  className="add-btn"
                  onClick={() => setShowAddForm(!showAddForm)}
                >
                  {showAddForm ? 'Cancel' : 'Add Department'}
                </button>
              </div>

              {showAddForm && (
                <div className="add-form-container">
                  <form onSubmit={handleAddDepartment} className="add-form">
                    <div className="form-group">
                      <input
                        type="text"
                        placeholder="Department Name"
                        value={newItem.name}
                        onChange={(e) => setNewItem({...newItem, name: e.target.value})}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <textarea
                        placeholder="Description"
                        value={newItem.specialization}
                        onChange={(e) => setNewItem({...newItem, specialization: e.target.value})}
                        required
                      />
                    </div>
                    <button type="submit" className="submit-btn">Add Department</button>
                  </form>
                </div>
              )}

              {loading ? (
                <div className="loading-spinner">Loading...</div>
              ) : error ? (
                <div className="error-message">{error}</div>
              ) : (
                <div className="departments-grid">
                  {departments.map(dept => (
                    <div key={dept._id} className="department-card">
                      <div className="department-header">
                        <h3>{dept.name}</h3>
                      </div>
                      <div className="department-info">
                        <p>{dept.description}</p>
                      </div>
                      <div className="department-actions">
                        <button 
                          className="edit-btn"
                          onClick={() => handleEditDepartment(dept._id)}
                        >
                          Edit
                        </button>
                        <button 
                          className="delete-btn"
                          onClick={() => handleDeleteDepartment(dept._id)}
                        >
                          Delete
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      {success && (
        <div className="success-message">
          {success}
          <button onClick={() => setSuccess('')}>×</button>
        </div>
      )}
    </div>
  );
};

export default HospitalAdminDashboard;
