import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './HospitalAdminDashboard.css';

const HospitalAdminDashboard = () => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('doctors');
  const [doctors, setDoctors] = useState([]);
  const [nurses, setNurses] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(true);
  const [hospital, setHospital] = useState(null);
  const [adminName, setAdminName] = useState('');
  const [showAddForm, setShowAddForm] = useState(false);
  const [newItem, setNewItem] = useState({
    name: '',
    email: '',
    password: '',
    departmentId: '',
    specialization: ''
  });

  useEffect(() => {
    const userRole = localStorage.getItem('userRole');
    const hospitalId = localStorage.getItem('hospitalId');
    const name = localStorage.getItem('userName');
    
    if (userRole !== 'HOSPITAL_ADMIN' || !hospitalId) {
      navigate('/login');
      return;
    }

    setAdminName(name || '');
    fetchHospitalDetails(hospitalId);
    fetchData();
  }, [navigate]);

  const fetchHospitalDetails = async (hospitalId) => {
    try {
      const response = await axios.get(`http://localhost:3000/api/auth/hospitals/${hospitalId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setHospital(response.data);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to fetch hospital details');
    }
  };

  const fetchData = async () => {
    setLoading(true);
    setError('');
    try {
      const hospitalId = localStorage.getItem('hospitalId');
      const token = localStorage.getItem('token');

      if (!hospitalId || !token) {
        throw new Error('Missing hospital ID or token');
      }

      // Fetch departments
      const departmentsResponse = await axios.get(`http://localhost:3000/api/dept/hospital/${hospitalId}`, {
        headers: { 
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      
      if (departmentsResponse.data) {
        setDepartments(departmentsResponse.data);
      }

      // Fetch doctors
      const doctorsResponse = await axios.get(`http://localhost:3000/api/doctor/hospital/${hospitalId}`, {
        headers: { 
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      
      if (doctorsResponse.data) {
        setDoctors(doctorsResponse.data);
      }

      // Fetch nurses
      const nursesResponse = await axios.get(`http://localhost:3000/api/nurse/hospital/${hospitalId}`, {
        headers: { 
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      
      if (nursesResponse.data) {
        setNurses(nursesResponse.data);
      }

    } catch (err) {
      console.error('Error fetching data:', err);
      if (err.response) {
        setError(err.response.data?.message || 'Failed to fetch data from server');
      } else if (err.request) {
        setError('No response received from server');
      } else {
        setError('Error setting up the request');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  const handleAddDoctor = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const hospitalId = localStorage.getItem('hospitalId');
      const token = localStorage.getItem('token');

      if (!hospitalId || !token) {
        throw new Error('Missing hospital ID or token');
      }

      const response = await axios.post('http://localhost:3000/api/doctor/create-doctor', {
        ...newItem,
        hospitalId,
        role: 'DOCTOR'
      }, {
        headers: { 
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.data) {
        setDoctors(prev => [...prev, response.data]);
        setSuccess('Doctor added successfully');
        setNewItem({ 
          name: '', 
          email: '', 
          password: '', 
          departmentId: '', 
          specialization: '' 
        });
        setShowAddForm(false);
      }
    } catch (err) {
      console.error('Error adding doctor:', err);
      setError(err.response?.data?.message || 'Failed to add doctor');
    }
  };

  const handleAddDepartment = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const hospitalId = localStorage.getItem('hospitalId');
      const token = localStorage.getItem('token');

      if (!hospitalId || !token) {
        throw new Error('Missing hospital ID or token');
      }

      const response = await axios.post('http://localhost:3000/api/dept/create-dept', {
        name: newItem.name,
        description: newItem.specialization,
        hospitalId
      }, {
        headers: { 
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.data) {
        setDepartments(prev => [...prev, response.data]);
        setSuccess('Department added successfully');
        setNewItem({ 
          name: '', 
          email: '', 
          password: '', 
          departmentId: '', 
          specialization: '' 
        });
        setShowAddForm(false);
      }
    } catch (err) {
      console.error('Error adding department:', err);
      setError(err.response?.data?.message || 'Failed to add department');
    }
  };

  const handleDeleteDoctor = async (doctorId) => {
    if (!window.confirm('Are you sure you want to delete this doctor?')) return;

    try {
      await axios.delete(`http://localhost:3000/api/doctor/${doctorId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setDoctors(prev => prev.filter(d => d._id !== doctorId));
      setSuccess('Doctor deleted successfully');
    } catch (err) {
