import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

interface Doctor {
  _id: string;
  name: string;
  email: string;
  specialization: string;
  departmentId: string;
  department?: {
    name: string;
  };
}

interface Nurse {
  _id: string;
  name: string;
  email: string;
  departmentId: string;
  department?: {
    name: string;
  };
}

interface Department {
  _id: string;
  name: string;
  description: string;
}

interface Hospital {
  _id: string;
  name: string;
}

const HospitalAdminDashboard: React.FC = () => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState<'doctors' | 'nurses' | 'departments'>('doctors');
  const [doctors, setDoctors] = useState<Doctor[]>([]);
  const [nurses, setNurses] = useState<Nurse[]>([]);
  const [departments, setDepartments] = useState<Department[]>([]);
  const [error, setError] = useState<string>('');
  const [success, setSuccess] = useState<string>('');
  const [loading, setLoading] = useState<boolean>(true);
  const [hospital, setHospital] = useState<Hospital | null>(null);
  const [adminName, setAdminName] = useState<string>('');

  // New staff form states
  const [newDoctor, setNewDoctor] = useState({
    name: '',
    email: '',
    password: '',
    specialization: '',
    departmentId: ''
  });

  const [newNurse, setNewNurse] = useState({
    name: '',
    email: '',
    password: '',
    departmentId: ''
  });

  const [newDepartment, setNewDepartment] = useState({
    name: '',
    description: ''
  });

  useEffect(() => {
    const userRole = localStorage.getItem('userRole');
    const hospitalId = localStorage.getItem('hospitalId');
    const name = localStorage.getItem('userName');
    
    if (userRole !== 'HOSPITAL_ADMIN' || !hospitalId) {
      navigate('/login');
      return;
    }

    setAdminName(name || '');
    fetchHospitalDetails(hospitalId);
    fetchData();
  }, [navigate]);

  const fetchHospitalDetails = async (hospitalId: string) => {
    try {
      const response = await axios.get(`http://localhost:3000/api/auth/hospitals/${hospitalId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setHospital(response.data);
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to fetch hospital details');
    }
  };

  const fetchData = async () => {
    setLoading(true);
    setError('');
    try {
      const hospitalId = localStorage.getItem('hospitalId');
      const token = localStorage.getItem('token');

      // Fetch departments first as they're needed for staff
      const departmentsResponse = await axios.get(`http://localhost:3000/api/dept/hospital/${hospitalId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setDepartments(departmentsResponse.data);

      // Fetch doctors
      const doctorsResponse = await axios.get(`http://localhost:3000/api/doctor/hospital/${hospitalId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setDoctors(doctorsResponse.data);

      // Fetch nurses
      const nursesResponse = await axios.get(`http://localhost:3000/api/nurse/hospital/${hospitalId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setNurses(nursesResponse.data);
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to fetch data');
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  const handleAddDoctor = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const hospitalId = localStorage.getItem('hospitalId');
      const response = await axios.post('http://localhost:3000/api/doctor', {
        ...newDoctor,
        hospitalId,
        role: 'DOCTOR'
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });

      setDoctors(prev => [...prev, response.data]);
      setSuccess('Doctor added successfully');
      setNewDoctor({ name: '', email: '', password: '', specialization: '', departmentId: '' });
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to add doctor');
    }
  };

  const handleAddNurse = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const hospitalId = localStorage.getItem('hospitalId');
      const response = await axios.post('http://localhost:3000/api/nurse', {
        ...newNurse,
        hospitalId,
        role: 'NURSE'
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });

      setNurses(prev => [...prev, response.data]);
      setSuccess('Nurse added successfully');
      setNewNurse({ name: '', email: '', password: '', departmentId: '' });
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to add nurse');
    }
  };

  const handleAddDepartment = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const hospitalId = localStorage.getItem('hospitalId');
      const response = await axios.post('http://localhost:3000/api/dept/create-dept', {
        ...newDepartment,
        hospitalId
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });

      setDepartments(prev => [...prev, response.data]);
      setSuccess('Department added successfully');
      setNewDepartment({ name: '', description: '' });
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to add department');
    }
  };

  const handleDeleteDoctor = async (doctorId: string) => {
    if (!window.confirm('Are you sure you want to delete this doctor?')) return;

    try {
      await axios.delete(`http://localhost:3000/api/doctor/${doctorId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setDoctors(prev => prev.filter(d => d._id !== doctorId));
      setSuccess('Doctor deleted successfully');
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to delete doctor');
    }
  };

  const handleDeleteNurse = async (nurseId: string) => {
    if (!window.confirm('Are you sure you want to delete this nurse?')) return;

    try {
      await axios.delete(`http://localhost:3000/api/nurse/${nurseId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setNurses(prev => prev.filter(n => n._id !== nurseId));
      setSuccess('Nurse deleted successfully');
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to delete nurse');
    }
  };

  const handleDeleteDepartment = async (departmentId: string) => {
    if (!window.confirm('Are you sure you want to delete this department?')) return;

    try {
      await axios.delete(`http://localhost:3000/api/dept/${departmentId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setDepartments(prev => prev.filter(d => d._id !== departmentId));
      setSuccess('Department deleted successfully');
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to delete department');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100">
      <header className="bg-gradient-to-r from-indigo-900 via-blue-800 to-indigo-900 text-white shadow-lg">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex justify-between items-center">
            <div className="space-y-1">
              <h1 className="text-2xl font-bold tracking-tight">Hospital Admin Dashboard</h1>
              {hospital && (
                <div className="text-blue-100">
                  <h2 className="text-lg font-medium tracking-wide">{hospital.name}</h2>
                </div>
              )}
            </div>
            <div className="flex items-center space-x-4">
              {adminName && (
                <div className="text-right">
                  <p className="text-blue-100 font-medium">Welcome, {adminName}</p>
                </div>
              )}
              <button 
                onClick={handleLogout}
                className="px-4 py-2 bg-white/10 hover:bg-white/20 border border-white/20 rounded-md transition-all duration-200 hover:scale-105 focus:outline-none focus:ring-2 focus:ring-white/50"
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <div className="bg-white rounded-xl shadow-sm p-6 text-center transform hover:-translate-y-1 transition-all duration-200 hover:shadow-md border border-gray-100">
            <div className="w-12 h-12 bg-indigo-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <i className="fas fa-user-md text-xl text-indigo-900"></i>
            </div>
            <h3 className="text-gray-600 text-sm font-medium">Total Doctors</h3>
            <p className="text-3xl font-bold text-indigo-900 mt-2">{doctors.length}</p>
          </div>
          <div className="bg-white rounded-xl shadow-sm p-6 text-center transform hover:-translate-y-1 transition-all duration-200 hover:shadow-md border border-gray-100">
            <div className="w-12 h-12 bg-indigo-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <i className="fas fa-user-nurse text-xl text-indigo-900"></i>
            </div>
            <h3 className="text-gray-600 text-sm font-medium">Total Nurses</h3>
            <p className="text-3xl font-bold text-indigo-900 mt-2">{nurses.length}</p>
          </div>
          <div className="bg-white rounded-xl shadow-sm p-6 text-center transform hover:-translate-y-1 transition-all duration-200 hover:shadow-md border border-gray-100">
            <div className="w-12 h-12 bg-indigo-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <i className="fas fa-hospital text-xl text-indigo-900"></i>
            </div>
            <h3 className="text-gray-600 text-sm font-medium">Total Departments</h3>
            <p className="text-3xl font-bold text-indigo-900 mt-2">{departments.length}</p>
          </div>
        </div>

        <div className="bg-white rounded-xl shadow-sm mb-8 overflow-hidden">
          <div className="flex flex-col sm:flex-row p-2 bg-gray-50">
            <button 
              className={`flex-1 px-4 py-3 text-sm font-medium rounded-lg transition-all duration-200 flex items-center justify-center space-x-2 ${
                activeTab === 'doctors' 
                  ? 'bg-indigo-900 text-white shadow-md' 
                  : 'text-gray-600 hover:bg-gray-100'
              }`}
              onClick={() => setActiveTab('doctors')}
            >
              <i className="fas fa-user-md"></i>
              <span>Manage Doctors</span>
            </button>
            <button 
              className={`flex-1 px-4 py-3 text-sm font-medium rounded-lg transition-all duration-200 flex items-center justify-center space-x-2 ${
                activeTab === 'nurses' 
                  ? 'bg-indigo-900 text-white shadow-md' 
                  : 'text-gray-600 hover:bg-gray-100'
              }`}
              onClick={() => setActiveTab('nurses')}
            >
              <i className="fas fa-user-nurse"></i>
              <span>Manage Nurses</span>
            </button>
            <button 
              className={`flex-1 px-4 py-3 text-sm font-medium rounded-lg transition-all duration-200 flex items-center justify-center space-x-2 ${
                activeTab === 'departments' 
                  ? 'bg-indigo-900 text-white shadow-md' 
                  : 'text-gray-600 hover:bg-gray-100'
              }`}
              onClick={() => setActiveTab('departments')}
            >
              <i className="fas fa-hospital"></i>
              <span>Manage Departments</span>
            </button>
          </div>
        </div>

        {error && (
          <div className="bg-red-50 border-l-4 border-red-500 p-4 mb-6 rounded-r-lg">
            <div className="flex">
              <div className="flex-shrink-0">
                <i className="fas fa-exclamation-circle text-red-500"></i>
              </div>
              <div className="ml-3">
                <p className="text-red-700">{error}</p>
              </div>
            </div>
          </div>
        )}
        {success && (
          <div className="bg-green-50 border-l-4 border-green-500 p-4 mb-6 rounded-r-lg">
            <div className="flex">
              <div className="flex-shrink-0">
                <i className="fas fa-check-circle text-green-500"></i>
              </div>
              <div className="ml-3">
                <p className="text-green-700">{success}</p>
              </div>
            </div>
          </div>
        )}

        {loading ? (
          <div className="text-center py-12">
            <div className="inline-block animate-spin rounded-full h-8 w-8 border-4 border-indigo-900 border-t-transparent"></div>
            <p className="mt-2 text-gray-600">Loading...</p>
          </div>
        ) : (
          <div className="bg-white rounded-xl shadow-sm overflow-hidden">
            {activeTab === 'doctors' && (
              <div className="p-6">
                <section className="bg-gray-50 rounded-xl p-6 mb-8 border border-gray-100">
                  <h2 className="text-xl font-semibold text-indigo-900 mb-6 flex items-center">
                    <i className="fas fa-user-md mr-2"></i>
                    Add New Doctor
                  </h2>
                  <form onSubmit={handleAddDoctor} className="space-y-6">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                      <div>
                        <label htmlFor="doctorName" className="block text-sm font-medium text-gray-700 mb-1">Name</label>
                        <input
                          type="text"
                          id="doctorName"
                          value={newDoctor.name}
                          onChange={(e) => setNewDoctor(prev => ({ ...prev, name: e.target.value }))}
                          className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition-colors duration-200"
                          required
                        />
                      </div>
                      <div>
                        <label htmlFor="doctorEmail" className="block text-sm font-medium text-gray-700 mb-1">Email</label>
                        <input
                          type="email"
                          id="doctorEmail"
                          value={newDoctor.email}
                          onChange={(e) => setNewDoctor(prev => ({ ...prev, email: e.target.value }))}
                          className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition-colors duration-200"
                          required
                        />
                      </div>
                    </div>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                      <div>
                        <label htmlFor="doctorPassword" className="block text-sm font-medium text-gray-700 mb-1">Password</label>
                        <input
                          type="password"
                          id="doctorPassword"
                          value={newDoctor.password}
                          onChange={(e) => setNewDoctor(prev => ({ ...prev, password: e.target.value }))}
                          className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition-colors duration-200"
                          required
                        />
                      </div>
                      <div>
                        <label htmlFor="specialization" className="block text-sm font-medium text-gray-700 mb-1">Specialization</label>
                        <input
                          type="text"
                          id="specialization"
                          value={newDoctor.specialization}
                          onChange={(e) => setNewDoctor(prev => ({ ...prev, specialization: e.target.value }))}
                          className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition-colors duration-200"
                          required
                        />
                      </div>
                    </div>
                    <div>
                      <label htmlFor="doctorDepartment" className="block text-sm font-medium text-gray-700 mb-1">Department</label>
                      <select
                        id="doctorDepartment"
                        value={newDoctor.departmentId}
                        onChange={(e) => setNewDoctor(prev => ({ ...prev, departmentId: e.target.value }))}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition-colors duration-200"
                        required
                      >
                        <option value="">Select Department</option>
                        {departments.map(dept => (
                          <option key={dept._id} value={dept._id}>{dept.name}</option>
                        ))}
                      </select>
                    </div>
                    <button 
                      type="submit"
                      className="inline-flex items-center px-4 py-2 border border-transparent rounded-lg shadow-sm text-sm font-medium text-white bg-indigo-900 hover:bg-indigo-800 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-all duration-200 hover:scale-105"
                    >
                      <i className="fas fa-plus mr-2"></i>
                      Add Doctor
                    </button>
                  </form>
                </section>

                <section>
                  <h2 className="text-xl font-semibold text-indigo-900 mb-6 flex items-center">
                    <i className="fas fa-list mr-2"></i>
                    Doctors List
                  </h2>
                  {doctors.length === 0 ? (
                    <div className="text-center py-12 bg-gray-50 rounded-xl text-gray-600 border border-gray-100">
                      <i className="fas fa-user-md text-4xl text-gray-400 mb-4"></i>
                      <p>No doctors registered yet.</p>
                    </div>
                  ) : (
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                      {doctors.map(doctor => (
                        <div key={doctor._id} className="bg-white border border-gray-200 rounded-xl shadow-sm hover:shadow-md transition-all duration-200">
                          <div className="px-4 py-3 border-b border-gray-200 flex justify-between items-center bg-gray-50 rounded-t-xl">
                            <h3 className="text-lg font-medium text-indigo-900">{doctor.name}</h3>
                            <button 
                              onClick={() => handleDeleteDoctor(doctor._id)}
                              className="text-red-500 hover:text-red-700 hover:bg-red-50 p-2 rounded-lg transition-colors duration-200"
                            >
                              <i className="fas fa-trash"></i>
                            </button>
                          </div>
                          <div className="p-4 space-y-2">
                            <p className="flex items-center text-gray-600">
                              <i className="fas fa-envelope w-5 text-indigo-900"></i>
                              {doctor.email}
                            </p>
                            <p className="flex items-center text-gray-600">
                              <i className="fas fa-stethoscope w-5 text-indigo-900"></i>
                              {doctor.specialization}
                            </p>
                            <p className="flex items-center text-gray-600">
                              <i className="fas fa-hospital w-5 text-indigo-900"></i>
                              {doctor.department?.name || 'Not assigned'}
                            </p>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </section>
              </div>
            )}

            {activeTab === 'nurses' && (
              <div className="p-6">
                <section className="bg-gray-50 rounded-xl p-6 mb-8 border border-gray-100">
                  <h2 className="text-xl font-semibold text-indigo-900 mb-6 flex items-center">
                    <i className="fas fa-user-nurse mr-2"></i>
                    Add New Nurse
                  </h2>
                  <form onSubmit={handleAddNurse} className="space-y-6">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                      <div>
                        <label htmlFor="nurseName" className="block text-sm font-medium text-gray-700 mb-1">Name</label>
                        <input
                          type="text"
                          id="nurseName"
                          value={newNurse.name}
                          onChange={(e) => setNewNurse(prev => ({ ...prev, name: e.target.value }))}
                          className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition-colors duration-200"
                          required
                        />
                      </div>
                      <div>
                        <label htmlFor="nurseEmail" className="block text-sm font-medium text-gray-700 mb-1">Email</label>
                        <input
                          type="email"
                          id="nurseEmail"
                          value={newNurse.email}
                          onChange={(e) => setNewNurse(prev => ({ ...prev, email: e.target.value }))}
                          className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition-colors duration-200"
                          required
                        />
                      </div>
                    </div>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                      <div>
                        <label htmlFor="nursePassword" className="block text-sm font-medium text-gray-700 mb-1">Password</label>
                        <input
                          type="password"
                          id="nursePassword"
                          value={newNurse.password}
                          onChange={(e) => setNewNurse(prev => ({ ...prev, password: e.target.value }))}
                          className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition-colors duration-200"
                          required
                        />
                      </div>
                      <div>
                        <label htmlFor="nurseDepartment" className="block text-sm font-medium text-gray-700 mb-1">Department</label>
                        <select
                          id="nurseDepartment"
                          value={newNurse.departmentId}
                          onChange={(e) => setNewNurse(prev => ({ ...prev, departmentId: e.target.value }))}
                          className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition-colors duration-200"
                          required
                        >
                          <option value="">Select Department</option>
                          {departments.map(dept => (
                            <option key={dept._id} value={dept._id}>{dept.name}</option>
                          ))}
                        </select>
                      </div>
                    </div>
                    <button 
                      type="submit"
                      className="inline-flex items-center px-4 py-2 border border-transparent rounded-lg shadow-sm text-sm font-medium text-white bg-indigo-900 hover:bg-indigo-800 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-all duration-200 hover:scale-105"
                    >
                      <i className="fas fa-plus mr-2"></i>
                      Add Nurse
                    </button>
                  </form>
                </section>

                <section>
                  <h2 className="text-xl font-semibold text-indigo-900 mb-6 flex items-center">
                    <i className="fas fa-list mr-2"></i>
                    Nurses List
                  </h2>
                  {nurses.length === 0 ? (
                    <div className="text-center py-12 bg-gray-50 rounded-xl text-gray-600 border border-gray-100">
                      <i className="fas fa-user-nurse text-4xl text-gray-400 mb-4"></i>
                      <p>No nurses registered yet.</p>
                    </div>
                  ) : (
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                      {nurses.map(nurse => (
                        <div key={nurse._id} className="bg-white border border-gray-200 rounded-xl shadow-sm hover:shadow-md transition-all duration-200">
                          <div className="px-4 py-3 border-b border-gray-200 flex justify-between items-center bg-gray-50 rounded-t-xl">
                            <h3 className="text-lg font-medium text-indigo-900">{nurse.name}</h3>
                            <button 
                              onClick={() => handleDeleteNurse(nurse._id)}
                              className="text-red-500 hover:text-red-700 hover:bg-red-50 p-2 rounded-lg transition-colors duration-200"
                            >
                              <i className="fas fa-trash"></i>
                            </button>
                          </div>
                          <div className="p-4 space-y-2">
                            <p className="flex items-center text-gray-600">
                              <i className="fas fa-envelope w-5 text-indigo-900"></i>
                              {nurse.email}
                            </p>
                            <p className="flex items-center text-gray-600">
                              <i className="fas fa-hospital w-5 text-indigo-900"></i>
                              {nurse.department?.name || 'Not assigned'}
                            </p>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </section>
              </div>
            )}

            {activeTab === 'departments' && (
              <div className="p-6">
                <section className="bg-gray-50 rounded-xl p-6 mb-8 border border-gray-100">
                  <h2 className="text-xl font-semibold text-indigo-900 mb-6 flex items-center">
                    <i className="fas fa-hospital mr-2"></i>
                    Add New Department
                  </h2>
                  <form onSubmit={handleAddDepartment} className="space-y-6">
                    <div>
                      <label htmlFor="departmentName" className="block text-sm font-medium text-gray-700 mb-1">Name</label>
                      <input
                        type="text"
                        id="departmentName"
                        value={newDepartment.name}
                        onChange={(e) => setNewDepartment(prev => ({ ...prev, name: e.target.value }))}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition-colors duration-200"
                        required
                      />
                    </div>
                    <div>
                      <label htmlFor="departmentDescription" className="block text-sm font-medium text-gray-700 mb-1">Description</label>
                      <textarea
                        id="departmentDescription"
                        value={newDepartment.description}
                        onChange={(e) => setNewDepartment(prev => ({ ...prev, description: e.target.value }))}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition-colors duration-200 min-h-[100px]"
                        required
                      />
                    </div>
                    <button 
                      type="submit"
                      className="inline-flex items-center px-4 py-2 border border-transparent rounded-lg shadow-sm text-sm font-medium text-white bg-indigo-900 hover:bg-indigo-800 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-all duration-200 hover:scale-105"
                    >
                      <i className="fas fa-plus mr-2"></i>
                      Add Department
                    </button>
                  </form>
                </section>

                <section>
                  <h2 className="text-xl font-semibold text-indigo-900 mb-6 flex items-center">
                    <i className="fas fa-list mr-2"></i>
                    Departments List
                  </h2>
                  {departments.length === 0 ? (
                    <div className="text-center py-12 bg-gray-50 rounded-xl text-gray-600 border border-gray-100">
                      <i className="fas fa-hospital text-4xl text-gray-400 mb-4"></i>
                      <p>No departments registered yet.</p>
                    </div>
                  ) : (
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                      {departments.map(department => (
                        <div key={department._id} className="bg-white border border-gray-200 rounded-xl shadow-sm hover:shadow-md transition-all duration-200">
                          <div className="px-4 py-3 border-b border-gray-200 flex justify-between items-center bg-gray-50 rounded-t-xl">
                            <h3 className="text-lg font-medium text-indigo-900">{department.name}</h3>
                            <button 
                              onClick={() => handleDeleteDepartment(department._id)}
                              className="text-red-500 hover:text-red-700 hover:bg-red-50 p-2 rounded-lg transition-colors duration-200"
                            >
                              <i className="fas fa-trash"></i>
                            </button>
                          </div>
                          <div className="p-4 space-y-2">
                            <p className="flex items-center text-gray-600">
                              <i className="fas fa-info-circle w-5 text-indigo-900"></i>
                              {department.description}
                            </p>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </section>
              </div>
            )}
          </div>
        )}
      </main>
    </div>
  );
};

export default HospitalAdminDashboard;
