import React, { useState } from 'react';
import axios from 'axios';

const AppointmentBooking = () => {
  const [patientId, setPatientId] = useState('');
  const [doctorId, setDoctorId] = useState('');
  const [date, setDate] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3004/api/appointment/book', {
        patientId,
        doctorId,
        date,
      });
      console.log('Appointment booked successfully:', response.data);
      // Handle successful booking (e.g., redirect or show success message)
    } catch (err) {
      setError('Failed to book appointment. Please try again.');
      console.error('Booking error:', err);
    }
  };

  return (
    <div>
      <h2>Book an Appointment</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Patient ID:</label>
          <input type="text" value={patientId} onChange={(e) => setPatientId(e.target.value)} required />
        </div>
        <div>
          <label>Doctor ID:</label>
          <input type="text" value={doctorId} onChange={(e) => setDoctorId(e.target.value)} required />
        </div>
        <div>
          <label>Date:</label>
          <input type="date" value={date} onChange={(e) => setDate(e.target.value)} required />
        </div>
        <button type="submit">Book Appointment</button>
      </form>
    </div>
  );
};

export default AppointmentBooking;
