import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin from '@fullcalendar/interaction';
import '@fullcalendar/core/main.css';
import '@fullcalendar/daygrid/main.css';
import '@fullcalendar/timegrid/main.css';
import './Scheduling.css';

interface Staff {
  id: string;
  name: string;
  type: 'DOCTOR' | 'NURSE';
  department: string;
}

interface Shift {
  id: string;
  staffId: string;
  staffName: string;
  staffType: 'DOCTOR' | 'NURSE';
  start: Date;
  end: Date;
  department: string;
}

const Scheduling: React.FC = () => {
  const [selectedDate, setSelectedDate] = useState<Date>(new Date());
  const [availableStaff, setAvailableStaff] = useState<Staff[]>([]);
  const [shifts, setShifts] = useState<Shift[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedStaffType, setSelectedStaffType] = useState<'DOCTOR' | 'NURSE'>('DOCTOR');

  useEffect(() => {
    fetchAvailableStaff();
    fetchShifts();
  }, [selectedDate, selectedStaffType]);

  const fetchAvailableStaff = async () => {
    try {
      const endpoint = selectedStaffType === 'DOCTOR' ? '/api/doctors/availabledates' : '/api/nurses/availabledates';
      const response = await fetch(endpoint, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
      });
      const data = await response.json();
      if (data.success) {
        setAvailableStaff(data.data);
      }
    } catch (error) {
      setError('Failed to fetch available staff');
      console.error('Error fetching available staff:', error);
    }
  };

  const fetchShifts = async () => {
    try {
      const response = await fetch(`/api/schedules?date=${selectedDate.toISOString()}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
      });
      const data = await response.json();
      if (data.success) {
        setShifts(data.data);
      }
    } catch (error) {
      setError('Failed to fetch shifts');
      console.error('Error fetching shifts:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleScheduleShift = async (staffId: string, startTime: string, endTime: string) => {
    try {
      const response = await fetch('/api/schedules', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          staffId,
          staffType: selectedStaffType,
          start: new Date(`${selectedDate.toISOString().split('T')[0]}T${startTime}`),
          end: new Date(`${selectedDate.toISOString().split('T')[0]}T${endTime}`),
        }),
      });

      if (response.ok) {
        fetchShifts();
      } else {
        setError('Failed to schedule shift');
      }
    } catch (error) {
      setError('Failed to schedule shift');
      console.error('Error scheduling shift:', error);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-gray-900"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="rounded-md bg-red-50 p-4">
        <div className="flex">
          <div className="ml-3">
            <h3 className="text-sm font-medium text-red-800">{error}</h3>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="px-4 sm:px-6 lg:px-8">
      <div className="sm:flex sm:items-center">
        <div className="sm:flex-auto">
          <h1 className="text-xl font-semibold text-gray-900">Staff Scheduling</h1>
          <p className="mt-2 text-sm text-gray-700">
            Schedule shifts for doctors and nurses. Select a date and staff member to create a new shift.
          </p>
        </div>
      </div>

      <div className="mt-8 grid grid-cols-1 gap-6 lg:grid-cols-2">
        {/* Staff Selection */}
        <div className="bg-white shadow rounded-lg p-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-lg font-medium text-gray-900">Available Staff</h2>
            <div className="flex space-x-4">
              <button
                onClick={() => setSelectedStaffType('DOCTOR')}
                className={`px-4 py-2 rounded-md ${
                  selectedStaffType === 'DOCTOR'
                    ? 'bg-indigo-600 text-white'
                    : 'bg-gray-100 text-gray-700'
                }`}
              >
                Doctors
              </button>
              <button
                onClick={() => setSelectedStaffType('NURSE')}
                className={`px-4 py-2 rounded-md ${
                  selectedStaffType === 'NURSE'
                    ? 'bg-indigo-600 text-white'
                    : 'bg-gray-100 text-gray-700'
                }`}
              >
                Nurses
              </button>
            </div>
          </div>

          <div className="space-y-4">
            {availableStaff.map((staff) => (
              <div
                key={staff.id}
                className="flex items-center justify-between p-4 bg-gray-50 rounded-lg"
              >
                <div>
                  <h3 className="text-sm font-medium text-gray-900">{staff.name}</h3>
                  <p className="text-sm text-gray-500">{staff.department}</p>
                </div>
                <div className="flex space-x-2">
                  <input
                    type="time"
                    className="rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                    onChange={(e) => handleScheduleShift(staff.id, e.target.value, '17:00')}
                  />
                  <button
                    onClick={() => handleScheduleShift(staff.id, '09:00', '17:00')}
                    className="inline-flex items-center px-3 py-2 border border-transparent text-sm leading-4 font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                  >
                    Schedule
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Calendar View */}
        <div className="bg-white shadow rounded-lg p-6">
          <h2 className="text-lg font-medium text-gray-900 mb-4">Schedule Calendar</h2>
          <div className="calendar-container">
            <FullCalendar
              plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
              initialView="timeGridWeek"
              headerToolbar={{
                left: 'prev,next today',
                center: 'title',
                right: 'timeGridWeek,timeGridDay',
              }}
              events={shifts.map((shift) => ({
                id: shift.id,
                title: `${shift.staffName} (${shift.staffType})`,
                start: shift.start,
                end: shift.end,
                backgroundColor: shift.staffType === 'DOCTOR' ? '#4F46E5' : '#059669',
              }))}
              selectable={true}
              selectMirror={true}
              dayMaxEvents={true}
              weekends={true}
              nowIndicator={true}
              height="auto"
              aspectRatio={1.35}
              allDaySlot={false}
              slotMinTime="06:00:00"
              slotMaxTime="20:00:00"
              expandRows={true}
              stickyHeaderDates={true}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Scheduling;
