import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Register.css';

interface RegisterFormData {
  name: string;
  email: string;
  password: string;
  confirmPassword: string;
  role: string;
  hospitalId?: string;
  specialization?: string;
  licenseNumber?: string;
}

const Register: React.FC = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState<RegisterFormData>({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
    role: 'patient',
    hospitalId: '',
    specialization: '',
    licenseNumber: ''
  });
  const [error, setError] = useState<string>('');
  const [success, setSuccess] = useState<string>('');
  const [loading, setLoading] = useState<boolean>(false);
  const [hospitals, setHospitals] = useState<Array<{ _id: string; name: string }>>([]);

  React.useEffect(() => {
    // Fetch hospitals if role is doctor, nurse, or hospital admin
    if (['doctor', 'nurse', 'hospital-admin'].includes(formData.role)) {
      fetchHospitals();
    }
  }, [formData.role]);

  const fetchHospitals = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/hospitals');
      setHospitals(response.data);
    } catch (err) {
      console.error('Error fetching hospitals:', err);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      setLoading(false);
      return;
    }

    try {
      let endpoint = '';
      const payload: any = {
        name: formData.name,
        email: formData.email,
        password: formData.password,
        role: formData.role.toUpperCase()
      };

      // Add role-specific fields
      switch (formData.role) {
        case 'doctor':
          endpoint = 'http://localhost:3000/api/doctors/register';
          payload.specialization = formData.specialization;
          payload.licenseNumber = formData.licenseNumber;
          payload.hospitalId = formData.hospitalId;
          break;
        case 'nurse':
          endpoint = 'http://localhost:3000/api/nurses/register';
          payload.hospitalId = formData.hospitalId;
          break;
        case 'patient':
          endpoint = 'http://localhost:3000/api/patients/register';
          break;
        case 'hospital-admin':
          endpoint = 'http://localhost:3000/api/auth/register';
          payload.hospitalId = formData.hospitalId;
          break;
        default:
          setError('Invalid role selected');
          setLoading(false);
          return;
      }

      const response = await axios.post(endpoint, payload);
      setSuccess('Registration successful! Please login.');
      
      // Clear form
      setFormData({
        name: '',
        email: '',
        password: '',
        confirmPassword: '',
        role: 'patient',
        hospitalId: '',
        specialization: '',
        licenseNumber: ''
      });

      // Redirect to login after 2 seconds
      setTimeout(() => {
        navigate('/login');
      }, 2000);
    } catch (err: any) {
      console.error('Registration error:', err);
      setError(err.response?.data?.message || 'Registration failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="register-container">
      <div className="register-box">
        <h2>Register</h2>
        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">{success}</div>}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="role">Role:</label>
            <select
              id="role"
              name="role"
              value={formData.role}
              onChange={handleChange}
              required
            >
              <option value="patient">Patient</option>
              <option value="doctor">Doctor</option>
              <option value="nurse">Nurse</option>
              <option value="hospital-admin">Hospital Admin</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="name">Full Name:</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="confirmPassword">Confirm Password:</label>
            <input
              type="password"
              id="confirmPassword"
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleChange}
              required
            />
          </div>

          {['doctor', 'nurse', 'hospital-admin'].includes(formData.role) && (
            <div className="form-group">
              <label htmlFor="hospitalId">Hospital:</label>
              <select
                id="hospitalId"
                name="hospitalId"
                value={formData.hospitalId}
                onChange={handleChange}
                required
              >
                <option value="">Select a hospital</option>
                {hospitals.map(hospital => (
                  <option key={hospital._id} value={hospital._id}>
                    {hospital.name}
                  </option>
                ))}
              </select>
            </div>
          )}

          {formData.role === 'doctor' && (
            <>
              <div className="form-group">
                <label htmlFor="specialization">Specialization:</label>
                <input
                  type="text"
                  id="specialization"
                  name="specialization"
                  value={formData.specialization}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="licenseNumber">License Number:</label>
                <input
                  type="text"
                  id="licenseNumber"
                  name="licenseNumber"
                  value={formData.licenseNumber}
                  onChange={handleChange}
                  required
                />
              </div>
            </>
          )}

          <button 
            type="submit" 
            className="register-button"
            disabled={loading}
          >
            {loading ? 'Registering...' : 'Register'}
          </button>
        </form>
        <p className="login-link">
          Already have an account? <a href="/login">Login here</a>
        </p>
      </div>
    </div>
  );
};

export default Register;
