import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './HospitalAdminDashboard.css';

interface Doctor {
  _id: string;
  name: string;
  email: string;
  specialization: string;
  departmentId: string;
  department?: {
    name: string;
  };
}

interface Nurse {
  _id: string;
  name: string;
  email: string;
  departmentId: string;
  department?: {
    name: string;
  };
}

interface Department {
  _id: string;
  name: string;
  description: string;
}

const HospitalAdminDashboard: React.FC = () => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState<'doctors' | 'nurses' | 'departments'>('doctors');
  const [doctors, setDoctors] = useState<Doctor[]>([]);
  const [nurses, setNurses] = useState<Nurse[]>([]);
  const [departments, setDepartments] = useState<Department[]>([]);
  const [error, setError] = useState<string>('');
  const [success, setSuccess] = useState<string>('');
  const [loading, setLoading] = useState<boolean>(true);

  // New staff form states
  const [newDoctor, setNewDoctor] = useState({
    name: '',
    email: '',
    password: '',
    specialization: '',
    departmentId: ''
  });

  const [newNurse, setNewNurse] = useState({
    name: '',
    email: '',
    password: '',
    departmentId: ''
  });

  const [newDepartment, setNewDepartment] = useState({
    name: '',
    description: ''
  });

  useEffect(() => {
    const userRole = localStorage.getItem('userRole');
    const hospitalId = localStorage.getItem('hospitalId');
    
    if (userRole !== 'HOSPITAL_ADMIN' || !hospitalId) {
      navigate('/login');
      return;
    }

    fetchData();
  }, [navigate]);

  const fetchData = async () => {
    setLoading(true);
    setError('');
    try {
      const hospitalId = localStorage.getItem('hospitalId');
      const token = localStorage.getItem('token');

      // Fetch departments first as they're needed for staff
      const departmentsResponse = await axios.get(`http://localhost:3000/api/dept/hospital/${hospitalId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setDepartments(departmentsResponse.data);

      // Fetch doctors
      const doctorsResponse = await axios.get(`http://localhost:3000/api/doctor/hospital/${hospitalId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setDoctors(doctorsResponse.data);

      // Fetch nurses
      const nursesResponse = await axios.get(`http://localhost:3000/api/nurse/hospital/${hospitalId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setNurses(nursesResponse.data);
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to fetch data');
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  const handleAddDoctor = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const hospitalId = localStorage.getItem('hospitalId');
      const response = await axios.post('http://localhost:3000/api/doctor', {
        ...newDoctor,
        hospitalId,
        role: 'DOCTOR'
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });

      setDoctors(prev => [...prev, response.data]);
      setSuccess('Doctor added successfully');
      setNewDoctor({ name: '', email: '', password: '', specialization: '', departmentId: '' });
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to add doctor');
    }
  };

  const handleAddNurse = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const hospitalId = localStorage.getItem('hospitalId');
      const response = await axios.post('http://localhost:3000/api/nurse', {
        ...newNurse,
        hospitalId,
        role: 'NURSE'
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });

      setNurses(prev => [...prev, response.data]);
      setSuccess('Nurse added successfully');
      setNewNurse({ name: '', email: '', password: '', departmentId: '' });
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to add nurse');
    }
  };

  const handleAddDepartment = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const hospitalId = localStorage.getItem('hospitalId');
      const response = await axios.post('http://localhost:3000/api/dept', {
        ...newDepartment,
        hospitalId
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });

      setDepartments(prev => [...prev, response.data]);
      setSuccess('Department added successfully');
      setNewDepartment({ name: '', description: '' });
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to add department');
    }
  };

  const handleDeleteDoctor = async (doctorId: string) => {
    if (!window.confirm('Are you sure you want to delete this doctor?')) return;

    try {
      await axios.delete(`http://localhost:3000/api/doctor/${doctorId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setDoctors(prev => prev.filter(d => d._id !== doctorId));
      setSuccess('Doctor deleted successfully');
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to delete doctor');
    }
  };

  const handleDeleteNurse = async (nurseId: string) => {
    if (!window.confirm('Are you sure you want to delete this nurse?')) return;

    try {
      await axios.delete(`http://localhost:3000/api/nurse/${nurseId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setNurses(prev => prev.filter(n => n._id !== nurseId));
      setSuccess('Nurse deleted successfully');
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to delete nurse');
    }
  };

  const handleDeleteDepartment = async (departmentId: string) => {
    if (!window.confirm('Are you sure you want to delete this department?')) return;

    try {
      await axios.delete(`http://localhost:3000/api/dept/${departmentId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setDepartments(prev => prev.filter(d => d._id !== departmentId));
      setSuccess('Department deleted successfully');
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to delete department');
    }
  };

  return (
    <div className="hospital-admin-dashboard">
      <header className="dashboard-header">
        <h1>Hospital Admin Dashboard</h1>
        <button onClick={handleLogout} className="logout-button">Logout</button>
      </header>

      <main className="dashboard-content">
        <div className="tabs">
          <button 
            className={`tab-button ${activeTab === 'doctors' ? 'active' : ''}`}
            onClick={() => setActiveTab('doctors')}
          >
            Manage Doctors
          </button>
          <button 
            className={`tab-button ${activeTab === 'nurses' ? 'active' : ''}`}
            onClick={() => setActiveTab('nurses')}
          >
            Manage Nurses
          </button>
          <button 
            className={`tab-button ${activeTab === 'departments' ? 'active' : ''}`}
            onClick={() => setActiveTab('departments')}
          >
            Manage Departments
          </button>
        </div>

        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">{success}</div>}

        {loading ? (
          <div className="loading-message">Loading...</div>
        ) : (
          <>
            {activeTab === 'doctors' && (
              <>
                <section className="add-section">
                  <h2>Add New Doctor</h2>
                  <form onSubmit={handleAddDoctor} className="add-form">
                    <div className="form-group">
                      <label htmlFor="doctorName">Name:</label>
                      <input
                        type="text"
                        id="doctorName"
                        value={newDoctor.name}
                        onChange={(e) => setNewDoctor(prev => ({ ...prev, name: e.target.value }))}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="doctorEmail">Email:</label>
                      <input
                        type="email"
                        id="doctorEmail"
                        value={newDoctor.email}
                        onChange={(e) => setNewDoctor(prev => ({ ...prev, email: e.target.value }))}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="doctorPassword">Password:</label>
                      <input
                        type="password"
                        id="doctorPassword"
                        value={newDoctor.password}
                        onChange={(e) => setNewDoctor(prev => ({ ...prev, password: e.target.value }))}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="specialization">Specialization:</label>
                      <input
                        type="text"
                        id="specialization"
                        value={newDoctor.specialization}
                        onChange={(e) => setNewDoctor(prev => ({ ...prev, specialization: e.target.value }))}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="doctorDepartment">Department:</label>
                      <select
                        id="doctorDepartment"
                        value={newDoctor.departmentId}
                        onChange={(e) => setNewDoctor(prev => ({ ...prev, departmentId: e.target.value }))}
                        required
                      >
                        <option value="">Select Department</option>
                        {departments.map(dept => (
                          <option key={dept._id} value={dept._id}>{dept.name}</option>
                        ))}
                      </select>
                    </div>
                    <button type="submit" className="add-button">Add Doctor</button>
                  </form>
                </section>

                <section className="list-section">
                  <h2>Doctors List</h2>
                  {doctors.length === 0 ? (
                    <div className="no-data-message">No doctors registered yet.</div>
                  ) : (
                    <div className="cards-grid">
                      {doctors.map(doctor => (
                        <div key={doctor._id} className="card">
                          <h3>{doctor.name}</h3>
                          <p><strong>Email:</strong> {doctor.email}</p>
                          <p><strong>Specialization:</strong> {doctor.specialization}</p>
                          <p><strong>Department:</strong> {doctor.department?.name || 'Not assigned'}</p>
                          <div className="card-actions">
                            <button 
                              className="delete-button"
                              onClick={() => handleDeleteDoctor(doctor._id)}
                            >
                              Delete
                            </button>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </section>
              </>
            )}

            {activeTab === 'nurses' && (
              <>
                <section className="add-section">
                  <h2>Add New Nurse</h2>
                  <form onSubmit={handleAddNurse} className="add-form">
                    <div className="form-group">
                      <label htmlFor="nurseName">Name:</label>
                      <input
                        type="text"
                        id="nurseName"
                        value={newNurse.name}
                        onChange={(e) => setNewNurse(prev => ({ ...prev, name: e.target.value }))}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="nurseEmail">Email:</label>
                      <input
                        type="email"
                        id="nurseEmail"
                        value={newNurse.email}
                        onChange={(e) => setNewNurse(prev => ({ ...prev, email: e.target.value }))}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="nursePassword">Password:</label>
                      <input
                        type="password"
                        id="nursePassword"
                        value={newNurse.password}
                        onChange={(e) => setNewNurse(prev => ({ ...prev, password: e.target.value }))}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="nurseDepartment">Department:</label>
                      <select
                        id="nurseDepartment"
                        value={newNurse.departmentId}
                        onChange={(e) => setNewNurse(prev => ({ ...prev, departmentId: e.target.value }))}
                        required
                      >
                        <option value="">Select Department</option>
                        {departments.map(dept => (
                          <option key={dept._id} value={dept._id}>{dept.name}</option>
                        ))}
                      </select>
                    </div>
                    <button type="submit" className="add-button">Add Nurse</button>
                  </form>
                </section>

                <section className="list-section">
                  <h2>Nurses List</h2>
                  {nurses.length === 0 ? (
                    <div className="no-data-message">No nurses registered yet.</div>
                  ) : (
                    <div className="cards-grid">
                      {nurses.map(nurse => (
                        <div key={nurse._id} className="card">
                          <h3>{nurse.name}</h3>
                          <p><strong>Email:</strong> {nurse.email}</p>
                          <p><strong>Department:</strong> {nurse.department?.name || 'Not assigned'}</p>
                          <div className="card-actions">
                            <button 
                              className="delete-button"
                              onClick={() => handleDeleteNurse(nurse._id)}
                            >
                              Delete
                            </button>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </section>
              </>
            )}

            {activeTab === 'departments' && (
              <>
                <section className="add-section">
                  <h2>Add New Department</h2>
                  <form onSubmit={handleAddDepartment} className="add-form">
                    <div className="form-group">
                      <label htmlFor="departmentName">Name:</label>
                      <input
                        type="text"
                        id="departmentName"
                        value={newDepartment.name}
                        onChange={(e) => setNewDepartment(prev => ({ ...prev, name: e.target.value }))}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="departmentDescription">Description:</label>
                      <textarea
                        id="departmentDescription"
                        value={newDepartment.description}
                        onChange={(e) => setNewDepartment(prev => ({ ...prev, description: e.target.value }))}
                        required
                      />
                    </div>
                    <button type="submit" className="add-button">Add Department</button>
                  </form>
                </section>

                <section className="list-section">
                  <h2>Departments List</h2>
                  {departments.length === 0 ? (
                    <div className="no-data-message">No departments registered yet.</div>
                  ) : (
                    <div className="cards-grid">
                      {departments.map(department => (
                        <div key={department._id} className="card">
                          <h3>{department.name}</h3>
                          <p><strong>Description:</strong> {department.description}</p>
                          <div className="card-actions">
                            <button 
                              className="delete-button"
                              onClick={() => handleDeleteDepartment(department._id)}
                            >
                              Delete
                            </button>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </section>
              </>
            )}
          </>
        )}
      </main>
    </div>
  );
};

export default HospitalAdminDashboard;
