import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

interface LoginFormData {
  email: string;
  password: string;
}

const Login: React.FC = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState<LoginFormData>({
    email: '',
    password: '',
  });
  const [error, setError] = useState<string>('');

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');

    try {
      // TODO: Replace with actual API call
      // This is a mock authentication logic
      const mockUsers = {
        'superadmin@hospital.com': { password: 'admin123', role: 'super-admin' },
        'hospitaladmin@hospital.com': { password: 'admin123', role: 'hospital-admin' },
        'doctor@hospital.com': { password: 'doctor123', role: 'doctor' },
        'nurse@hospital.com': { password: 'nurse123', role: 'nurse' },
        'patient@hospital.com': { password: 'patient123', role: 'patient' },
      };

      const user = mockUsers[formData.email as keyof typeof mockUsers];

      if (user && user.password === formData.password) {
        // Store user role in localStorage
        localStorage.setItem('userRole', user.role);
        localStorage.setItem('userEmail', formData.email);

        // Redirect based on role
        switch (user.role) {
          case 'super-admin':
            navigate('/super-admin-dashboard');
            break;
          case 'hospital-admin':
            navigate('/admin-dashboard');
            break;
          case 'doctor':
            navigate('/doctor-dashboard');
            break;
          case 'nurse':
            navigate('/nurse-dashboard');
            break;
          case 'patient':
            navigate('/patient-dashboard');
            break;
          default:
            navigate('/');
        }
      } else {
        setError('Invalid email or password');
      }
    } catch (err) {
      setError('An error occurred during login');
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Hospital Management System</h2>
        <h3>Login</h3>
        {error && <div className="error-message">{error}</div>}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
              placeholder="Enter your email"
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              placeholder="Enter your password"
            />
          </div>
          <button type="submit" className="login-button">Login</button>
        </form>
        <div className="login-info">
          <p>Demo Credentials:</p>
          <ul>
            <li>Super Admin: superadmin@hospital.com / admin123</li>
            <li>Hospital Admin: hospitaladmin@hospital.com / admin123</li>
            <li>Doctor: doctor@hospital.com / doctor123</li>
            <li>Nurse: nurse@hospital.com / nurse123</li>
            <li>Patient: patient@hospital.com / patient123</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Login;
